function hoverTouchUnstick(){if("ontouchstart"in document.documentElement)for(var e=document.styleSheets.length-1;e>=0;e--){var t=document.styleSheets[e];if(t.cssRules)for(var s=t.cssRules.length-1;s>=0;s--){var c=t.cssRules[s];c.selectorText&&(c.selectorText=c.selectorText.replace(":hover",":active"))}}}